import React from "react";
import styled from "styled-components";

function Cart() {
  return (
    <Wrapper>
      <CartContainer>
        <ProductCartInfo>
          <div className="productInfo">
            <div className="productName">
              <span>Filtre Kahve</span>
            </div>
            <div className="thermosLitre">
              <span>1 Litre</span>
            </div>
            <div className="makeTime">
              <div className="totalTime">
                <span>25 dk</span>
              </div>
              <div className="deliveryAndMakeTime">
                <span>
                  Hazırlanış: 10dk <br />
                  Teslimat: 15dk
                </span>
              </div>
            </div>
          </div>
          <div className="remove">
            <span>Sil</span>
          </div>
        </ProductCartInfo>
      </CartContainer>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  width: 100%;
  margin-right: 20px;
  margin-left: 10px;
border-radius:10px;
  border: 1px solid brown;
`;
const CartContainer = styled.div`
  width: 200px;
  border-top: 2px solid brown;
  margin-top: 10px;
  display: flex;
  justify-content: center;
  align-items: center;

  margin-left: 5px;
`;

const ProductCartInfo = styled.div`
display:flex;
flex-direction:row;
margin-left:0px;
  margin-top: 10px;
  .productInfo{
      display:flex;
      flex-direction:column;
      margin-bottom:30px;
      .productName{

          margin-right:40px;
          span{
            font-size:20px;
            font-weight:600;
          }
      }
      .thermosLitre{
          margin-top:4px;
          span{
            font-size:15px;
          color:brown;
          font-weight:800;
          }

      }
      .makeTime{
          margin-top:5px;
          .totalTime{
              margin-bottom:4px;
              span{
                  font-size:20px;
              }
              .deliveryAndMakeTime{
                  span{
                    color:#bbbbbb
                  }
              }
          }

      }
  }
`;
export default Cart;
