import React from "react";
import styled from "styled-components";
import {Link} from "react-router-dom";
function CartConfirm() {
  return (
    <Wrapper>
      <CartConfirmContainer>
        <HeaderContainer>
          <div className="pageTitle">
            <span>bitermoscoffe</span>
          </div>
          <div className="deliveryTimeHeader">
            <span>10-20dk</span>
          </div>
        </HeaderContainer>
        <Order>
          <OrderDec>
            <div className="deliveryTime">
              <div className="deliveryTimeTitle">
                <span>Telimat Yöntemi</span>
              </div>
              <div className="deliveryTimeChoose">
                <div>
                  <input type="checkbox" id="now" />
                  <label htmlFor="now">Şimdi Teslim Et</label>
                </div>
                <div>
                  <input type="checkbox" id="time" />
                  <label htmlFor="time">Zaman Belirleme</label>
                </div>
              </div>
            </div>
            <div className="addNote">
              <div className="addNoteTitle">
                <span>Not Ekle</span>
              </div>
              <div className="add">
                <textarea placeholder="Eklemek İstediğiniz Notu Yazınız."></textarea>
              </div>
            </div>
          </OrderDec>
          <OrderUserDec>
            <div className="orderUserAdress">
              <div className="titleAdress">
                <span>Teslimat Adresi</span>
              </div>
              <div className="userAdress">
                <span>
                  Çoban Hasso Sokak Hidayet Mahallesi Doğan Apt No:7 Daire:3
                </span>
              </div>
            </div>
          </OrderUserDec>

        </Order>
        <OrderButton>
        <Link>
        <button>
        Sipariş Ver<br/>60 tl
        </button>
        </Link>

        </OrderButton>
      </CartConfirmContainer>
    </Wrapper>
  );
}
const Wrapper = styled.div``;
const CartConfirmContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;
const HeaderContainer = styled.div`
  width: 100%;

  background-color: #6f4e37;
  padding: 15px 20px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  width: 100%;
  z-index: 1000;
  top: 0;
  left: 0;

  .pageTitle {
    span {
      color: white;
      font-size: 30px;
    }
  }
  .deliveryTimeHeader {
    border: 1px solid #29bb89;
    padding: 10px 10px;
    border-radius: 10px;
    background-color: #29bb89;
    height: 100%;
    span {
      color: white;
      font-size: 19px;
      font-weight: 700;
    }
  }
`;
const Order = styled.div`
  margin-top: 120px;
  display: flex;
  flex-direction: row;
  justify-content: space-around;
`;
const OrderDec = styled.div`
  display: flex;
  flex-direction: column;
  .deliveryTime {
    width: 100%;
    display: flex;
    flex-direction: column;
    height: 100%;
    .deliveryTimeTitle {
      span {
        font-size: 20px;
        font-weight: 600;
      }
    }
    .deliveryTimeChoose {
      display: flex;
      flex-direction: column;
      width: 100%;
      padding: 10px 50px;
      padding-right: 180px;
      height: 100%;
      border: 2px solid #6f4e37;
      border-radius: 5px;
      margin-bottom: 40px;
      margin-top: 30px;
      div {
        font-size: 18px;

        label {
          margin-left: 20px;

          :hover {
            cursor: pointer;
          }
        }

        input {
          font-size: 19px;
          :hover {
            cursor: pointer;
          }
        }
      }
    }
  }
  .addNote {
    display: flex;
    flex-direction: column;
    justify-content: center;
    width: 100%;
    height: 100%;
    .addNoteTitle {
      span {
        font-size: 20px;
        font-weight: 600;
      }
      margin-bottom: 20px;
    }
    .add {
      width: 100%;

      height: 100%;
      border: 2px solid #6f4e37;
      border-radius: 5px;
      textarea {
        border: none;
        outline: none;
        width: 100%;
        padding: 10px 20px;
      }
    }
  }
`;
const OrderUserDec = styled.div`
.orderUserAdress{
    display:flex;
    flex-direction:column;
    .titleAdress{
        margin-bottom:20px;
        span{
            font-size:20px;
            font-weight:600;
        }
    }

    .userAdress{
        width:100%;
        height:100%;
        border:2px solid #6f4e37;
        padding:10px 20px;
        border-radius:5px;
    }
}

`;

const OrderButton = styled.div`
display:flex;
margin-top:60px;
justify-content:center;
 button{
     border:none;
     padding:10px 50px;
     color:white;
     background-color:#54e346;
     border-radius:10px;
     font-size:20px;
     font-weight:600;
     :hover{
         background-color:#03c03c

     }

 }

`
export default CartConfirm;
